var sockets = require('./index');

sockets.listen(process.argv[2] || 9999, function(socket) {
	socket.on('message', function(message) {
		if (message.method === 'echo') {
			socket.send(message);
			return;
		}
		if (message.method === 'flood') {
			for (var i = 0; i < 20; i++) {
				socket.send({data:i});
			}
			return;
		}
		if (message.method === 'close') {
			socket.destroy();
			return;
		}
	});	
});
